import Head from 'next/head'
import Link from 'next/link'
import styled from 'styled-components'

const IMAGE_SOURCE = '/image/godchanho.gif'
const loveMonday1 = '/image/love-monday.png'
const loveMonday2 = '/image/love-monday2.png'
const BACKGROUND_COLOR = 'rgb(23, 24, 41)'
const Container = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: ${BACKGROUND_COLOR};

  .text {
    margin: 20px 0px;
    font-size: 15px;
    color: white;
  }

  .img-wrapper {
    display: flex;
    flex-direction: row;
    justify-content: center;
    width: 100%;
  }
`

type ReflectImageProps = {
  image: string
}
const ReflectImage = styled.div<ReflectImageProps>`
  margin: 0px 20px;
  border-radius: 10px;
  box-shadow: 0px 2px 16px rgba(0, 0, 0, 0.8),
    0px 2px 8px rgba(255, 255, 255, 0.5);
  background-image: url('${(props) => props.image}');
  background-size: 100%;
  background-repeat: no-repeat;
  width: 200px;
  height: 200px;
  position: relative;
  transition: transform 200ms ease;

  &:hover {
    transform: scale(1.03);
    box-shadow: 0px 2px 16px rgba(0, 0, 0, 0.8),
      0px 2px px rgba(255, 255, 255, 0.5);
  }
  &::after {
    content: '';
    background-image: inherit;
    width: inherit;
    height: 40%;
    background-size: 100%;
    background-repeat: no-repeat;
    position: absolute;
    bottom: -60%;
    transform: scaleY(-1);
    background-position: bottom;
  }

  &::before {
    content: '';
    width: inherit;
    height: 42%;
    position: absolute;
    bottom: -60%;
    background: linear-gradient(
      to bottom,
      rgba(23, 24, 41, 0.3),
      ${BACKGROUND_COLOR}
    );
    z-index: 1;
  }
`
export default function Home() {
  return (
    <Container>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div className="img-wrapper">
        <ReflectImage image={IMAGE_SOURCE}></ReflectImage>
        <ReflectImage image={loveMonday1}></ReflectImage>
        <ReflectImage image={loveMonday2}></ReflectImage>
      </div>
      <div className="text">안녕하세요 이찬호입니다.</div>
    </Container>
  )
}
