import Head from 'next/head'
import Link from 'next/link'
import styled from 'styled-components'

const IMAGE_SOURCE = '/image/godchanho.gif'

const Container = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #171829;

  .text {
    margin: 20px 0px;
    font-size: 15px;
    color: white;
  }
  .reflect-img {
    border-radius: 10px;
    box-shadow: 0px 2px 16px #ffffff;
    background-image: url('${IMAGE_SOURCE}');
    background-size: 100%;
    background-repeat: no-repeat;
    width: 200px;
    height: 200px;
    position: relative;

    &::after {
      content: '';
      background-image: inherit;
      width: inherit;
      height: 40%;
      background-size: 100%;
      background-repeat: no-repeat;
      position: absolute;
      bottom: -40%;
      transform: scaleY(-1);
    }
  }
`
export default function Home() {
  return (
    <Container>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div className="reflect-img" />
      <div className="text">안녕하세요 이찬호입니다.</div>
    </Container>
  )
}
