import Head from 'next/head'
import Link from 'next/link'
import styled from 'styled-components'

const Container = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #171829;

  .text {
    margin: 20px 0px;
    font-size: 15px;
    color: white;
  }
  .reflect-img {
    border-radius: 10px;
    box-shadow: 0px 2px 16px #ffffff;
    width: 200px;
  }
  .img-reflection {
    margin: 20px 0;
    border-radius: 10px;
    box-shadow: 0px 2px 16px #ffffff;
    width: 200px;
    transform: rotate(180deg);
  }
`
export default function Home() {
  return (
    <Container>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div className="reflect-img" src="/image/godchanho.gif" />
      <div className="text">안녕하세요 이찬호입니다.</div>
    </Container>
  )
}
